import axios, { AxiosResponse } from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { genreDTO } from "./genres.mode";
import { urlGenres } from "../endpoints";
import GenericList from "../utils/GenericList";
import Button from "../utils/Button";
import Pagination from "../utils/Pagination";

export default function IndexGenres() {
  const [genres, setGenres] = useState<genreDTO[]>(); 
  const [totalAmountOfPages, setTotalAmountOfPages] = useState(0);
  const [recordsPerPage, setRecordsPerPage] = useState(2);
  const [page, setPage] = useState(1);

  useEffect(() => {
    axios.get(urlGenres, {
      params: {page, recordsPerPage}
    }).then((response: AxiosResponse<genreDTO[]>) => {
        const totalAmountOfRecords = 
          parseInt(response.headers["totalamountofrecords"],10);
      setTotalAmountOfPages(Math.ceil(totalAmountOfRecords / recordsPerPage));
      setGenres(response.data);
    });
  }, [page, recordsPerPage]);

  return (
    <>
      <h3>Genres</h3>
      <Link className="btn btn-primary" to="/genres/create">
        Create genre
      </Link>

      <div className="mb-3" style={{width: '150px'}}>
        <label>Records per page:</label>
        <select 
          className="form-control"
          defaultValue={5}
          onChange={e => {
            setPage(1);
            setRecordsPerPage(parseInt(e.currentTarget.value, 10));
          }}
        >
            <option value={5}>5</option>
            <option value={10}>10</option>
            <option value={25}>25</option>
            <option value={50}>50</option>
        </select>

      </div>

      <Pagination
        currentPage={page}
        totalAmountOfPages={totalAmountOfPages}
        onChange={newPage => setPage(newPage)}
      />

      <GenericList list={genres}>
        <table className="table table-striped">
          <thead>
            <tr>
              <th></th>
              <th>Name</th>
            </tr>
          </thead>
          <tbody>
            {genres?.map((genre) => (
              <tr key={genre.id}>
                <td>
                  <Link className="btn btn-success" to={`/genres/${genre.id}`}>
                    Edit
                  </Link>

                  <Button className="btn btn-danger">Delete</Button>
                </td>
                <td>{genre.name}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </GenericList>
    </>
  );
}
