import { Typeahead } from "react-bootstrap-typeahead";
import { actorMovieDTO } from "../actors/actors.mode";

export default function TypeAheadActors(props: typeAheadActorsProps){

    const actors: actorMovieDTO[] = [{
        id: 1, name: 'Tom Holland', character: '', picture: 'https://upload.wikimedia.org/wikipedia/commons/thumb/3/3c/Tom_Holland_by_Gage_Skidmore.jpg/220px-Tom_Holland_by_Gage_Skidmore.jpg'
    },
    {
        id: 1, name: 'Keanu Reeves', character: '', picture: 'https://upload.wikimedia.org/wikipedia/commons/thumb/3/33/Reuni達o_com_o_ator_norte-americano_Keanu_Reeves_%2846806576944%29_%28cropped%29.jpg/220px-Reuni達o_com_o_ator_norte-americano_Keanu_Reeves_%2846806576944%29_%28cropped%29.jpg'
    },
    {
        id: 1, name: 'Christian Bale', character: '', picture: 'https://upload.wikimedia.org/wikipedia/commons/thumb/0/0a/Christian_Bale-7837.jpg/220px-Christian_Bale-7837.jpg'
    }]

    return (
        <div className="mb-3">
            <label>{props.displayName}</label>
            <Typeahead 
                id="typeahead"
                onChange={actors => { 
                    if(props.actors.findIndex(x => x.id === actors[0].id) === -1){
                        props.onAdd([...props.actors, actors[0]])
                    }
                    console.log(actors)}}
                options={actors}
                labelKey={actor => actor.name}
                filterBy={['name']}
                placeholder='Write the name of the actor...'
                minLength={1}
                flip={true}
                renderMenuItemChildren={actor => (
                    <>
                        <img alt="actor" src={actor.picture}
                        style={{
                            height: '64px',
                            marginRight: '10px',
                            width: '64px'
                        }}
                        />
                        <span>{actor.name}</span>
                    </>
                )}
            />
        </div>
    )
}

interface typeAheadActorsProps{
    displayName: string;
    actors: actorMovieDTO[];
    onAdd(actors: actorMovieDTO[]): void
}
