import { Form, Formik, FormikHelpers } from "formik";
import { Link } from "react-router-dom";
import TextField from "../forms/TextField";
import Button from "../utils/Button";
import { actorCreationDTO } from "./actors.mode";
import * as Yup from 'yup';
import DateField from "../forms/DateField";
import ImageField from "../forms/ImageField";

export default function ActorForm(props: actorFormProps) {
  return (
    <Formik initialValues={props.model} onSubmit={props.onSubmit} validationSchema={Yup.object({
        name: Yup.string().required('This field is required').firstLetterUppercase(),
        dateOfBirth: Yup.date().nullable().required('This field is required')
    })}>
      {(formikProps) => (
        <Form>
          <TextField displayName="Name" field="name" />
          <DateField displayName="Date of Birth" field="dateOfBirth"/>
          <ImageField displayName="Picture"/>

          <Button disabled={formikProps.isSubmitting} type="submit">
            Save Changes
          </Button>
          <Link to="/actors" className="btn btn-secondary">Cancel</Link>
        </Form>
      )}
    </Formik>
  );
}

interface actorFormProps {
  model: actorCreationDTO;
  onSubmit(
    values: actorCreationDTO,
    action: FormikHelpers<actorCreationDTO>
  ): void;
}
