import { ChangeEvent, useState } from "react";

export default function ImageField(props: imageFieldProps){

    const [imageBase64, setImageBase64] = useState('');

    const divStyle = {marginTop: '10px'};
    const imgStyle = {width: '450px'};

    const handleOnChange = (eventsArgs: ChangeEvent<HTMLInputElement>) => {
        if (eventsArgs.currentTarget.files){
            const file = eventsArgs.currentTarget.files[0];
            if (file){
                toBase64(file).then((base64Representation: string) => setImageBase64(base64Representation))
                .catch(error => console.error(error))
            } else {
                setImageBase64('');
            }
        }
    }

    const toBase64 = (file: File) => {
        return new Promise<string>((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => resolve(reader.result as string);
            reader.onerror = (error) => reject(error);
        })
    }

    return (
        <div>
            <label>{props.displayName}</label>
            <div>
                <input type="file" accept=".jpg, .jpeg, .png" onChange={handleOnChange}></input>
            </div>
            {imageBase64 ? 
            <div>
                <div style={divStyle}>
                    <img style={imgStyle} src={imageBase64} alt="selected" />
                </div>
            </div> : null}
        </div>
    )
}

interface imageFieldProps{
    displayName: string;
}
